int main() {
    int N;
    cin &gt;&gt; N;
    cout &lt;&lt; "Your answer: " &lt;&lt; fibonacci(N) &lt;&lt; endl;
    int fib_prev = 0, fib_curr = 1;
    if (N == 0) {
        cout &lt;&lt; "Correct answer: " &lt;&lt; fib_prev &lt;&lt; endl;
    } else if (N == 1) {
        cout &lt;&lt; "Correct answer: " &lt;&lt; fib_curr &lt;&lt; endl;
    } else {
        for (int i = 2; i &lt;= N; ++i) {
            int fib_next = (fib_prev + fib_curr) % MOD;
            fib_prev = fib_curr;
            fib_curr = fib_next;
        }
        cout &lt;&lt; "Correct answer: " &lt;&lt; fib_curr &lt;&lt; endl;
    }
    return 0;
}
