C++<pre><code>// 多处用到对 10^9 + 7 取模，所以定义成常量
#define MOD 1000000007

struct Matrix {
    // 为了防止乘法时溢出，改用 long long
    long long m[2][2];
};

Matrix multiply(const Matrix &amp;a, const Matrix &amp;b) {
    Matrix c;
    for (int i = 0; i &lt; 2; ++i) {
        for (int j = 0; j &lt; 2; ++j) {
            // 时刻注意取模
            c.m[i][j] = a.m[i][0] * b.m[0][j] % MOD
                      + a.m[i][1] * b.m[1][j] % MOD;
            c.m[i][j] %= MOD;
        }
    }
    return c;
}

Matrix power(Matrix a, long long n) {
    // 终止条件判断
    if (n == 1) {
        return a;
    }
    Matrix result;
    result = power(a, n / 2);
    result = multiply(result, result);
    if (n % 2) { // n 是奇数
        result = multiply(result, a);
    }
    return result;
}

int fibonacci(long long n) {
    // 特殊情况的处理
    if (n == 0) return 0;
    if (n == 1) return 1;
    Matrix a;
    a.m[0][0] = 1; a.m[0][1] = 1;
    a.m[1][0] = 1; a.m[1][1] = 0;
    Matrix result;
    result = power(a, n-1);
    return (int)result.m[0][0];
}
</code></pre>

C++<pre><code>int cakeTower(int n, int T, int K, 
              const vector&lt;int&gt; &amp;h, 
              const vector&lt;int&gt; &amp;v) {
    int f1[2005] = {0}, f2[1005] = {0}, ans = 0;
    for (int i = 0; i &lt; n; ++i) {
        for (int j = h[i]; j &lt;= 2 * T; ++j) { // 可能放大蛋糕前会溢出一点高度
            f1[j] = max(f1[j], f1[j - h[i]] + v[i]);
        }
    }
    for (int j = 0; j &lt;= T; ++j) ans = max(ans, f1[j]);

    for (int j = K; j &lt;= T; ++j) {
        for(int i = 0; i &lt; n; ++i) {
            if (h[i] &lt; K) continue;
            if ((j - h[i]) % 4 != 0) continue;
            f2[j] = max(f2[j], f1[(j - h[i]) * 5 / 4] + v[i]);
        }
    }
    for (int j = K; j &lt;= T; ++j) ans = max(ans, f2[j]);
    return ans;
}
