C++<pre><code>#ifndef DS_EXCEPTIONS_H
#define DS_EXCEPTIONS_H

class UnderflowException { };
class IllegalArgumentException { };
class ArrayIndexOutOfBoundsException { };
class IteratorOutOfBoundsException { };
class IteratorMismatchException { };
class IteratorUninitializedException { };

#endif

#ifndef BINARY_SEARCH_TREE_H
#define BINARY_SEARCH_TREE_H

#include &lt;algorithm&gt;
#include &lt;vector&gt;
#include &lt;iostream&gt;
#include &lt;functional&gt;
using namespace std;       

// BinarySearchTree class
//
// CONSTRUCTION: zero parameter
//
// ******************PUBLIC OPERATIONS*********************
// void insert( x )       --&gt; Insert x
// void remove( x )       --&gt; Remove x
// bool contains( x )     --&gt; Return true if x is present
// Comparable findMin( )  --&gt; Return smallest item
// Comparable findMax( )  --&gt; Return largest item
// boolean isEmpty( )     --&gt; Return true if empty; else false
// void makeEmpty( )      --&gt; Remove all items
// void printTree( )      --&gt; Print tree in sorted order
// ******************ERRORS********************************
// Throws UnderflowException as warranted

template &lt;typename Comparable&gt;
class BinarySearchTree
{
  public:
    BinarySearchTree( ) : root{ nullptr }
    {
    }

    /**
     * Copy constructor
     */
    BinarySearchTree( const BinarySearchTree &amp; rhs ) : root{ nullptr }
    {
        root = clone( rhs.root );
    }

    /**
     * Move constructor
     */
    BinarySearchTree( BinarySearchTree &amp;&amp; rhs ) : root{ rhs.root }
    {
        rhs.root = nullptr;
    }

    /**
     * Destructor for the tree
     */
    ~BinarySearchTree( )
    {
        makeEmpty( );
    }

    /**
     * Copy assignment
     */
    BinarySearchTree &amp; operator=( const BinarySearchTree &amp; rhs )
    {
        BinarySearchTree copy = rhs;
        std::swap( *this, copy );
        return *this;
    }

    /**
     * Move assignment
     */
    BinarySearchTree &amp; operator=( BinarySearchTree &amp;&amp; rhs )
    {
        std::swap( root, rhs.root );       
        return *this;
    }


    /**
     * Find the smallest item in the tree.
     * Throw UnderflowException if empty.
     */
    const Comparable &amp; findMin( ) const
    {
        if( isEmpty( ) )
            throw UnderflowException{ };
        return findMin( root )-&gt;element;
    }

    /**
     * Find the largest item in the tree.
     * Throw UnderflowException if empty.
     */
    const Comparable &amp; findMax( ) const
    {
        if( isEmpty( ) )
            throw UnderflowException{ };
        return findMax( root )-&gt;element;
    }

    /**
     * Returns true if x is found in the tree.
     */
    bool contains( const Comparable &amp; x ) const
    {
        return contains( x, root );
    }

    /**
     * Test if the tree is logically empty.
     * Return true if empty, false otherwise.
     */
    bool isEmpty( ) const
    {
        return root == nullptr;
    }

    /**
     * Print the tree contents in sorted order.
     */
    void printTree( ostream &amp; out = cout ) const
    {
        if( isEmpty( ) )
            out &lt;&lt; "Empty tree" &lt;&lt; endl;
        else
            printTree( root, out );
    }

    /**
     * Make the tree logically empty.
     */
    void makeEmpty( )
    {
        makeEmpty( root );
    }

    /**
     * Insert x into the tree; duplicates are ignored.
     */
    void insert( const Comparable &amp; x )
    {
        insert( x, root );
    }

    /**
     * Insert x into the tree; duplicates are ignored.
     */
    void insert( Comparable &amp;&amp; x )
    {
        insert( std::move( x ), root );
    }

    /**
     * Remove x from the tree. Nothing is done if x is not found.
     */
    void remove( const Comparable &amp; x )
    {
        remove( x, root );
    }


  private:
    struct BinaryNode
    {
        Comparable element;
        BinaryNode *left;
        BinaryNode *right;

        BinaryNode( const Comparable &amp; theElement, BinaryNode *lt, BinaryNode *rt )
          : element{ theElement }, left{ lt }, right{ rt } { }

        BinaryNode( Comparable &amp;&amp; theElement, BinaryNode *lt, BinaryNode *rt )
          : element{ std::move( theElement ) }, left{ lt }, right{ rt } { }
    };

    BinaryNode *root;


    /**
     * Internal method to insert into a subtree.
     * x is the item to insert.
     * t is the node that roots the subtree.
     * Set the new root of the subtree.
     */
    void insert( const Comparable &amp; x, BinaryNode * &amp; t )
    {
        if( t == nullptr )
            t = new BinaryNode{ x, nullptr, nullptr };
        else if( x &lt; t-&gt;element )
            insert( x, t-&gt;left );
        else if( t-&gt;element &lt; x )
            insert( x, t-&gt;right );
        else
            ;  // Duplicate; do nothing
    }

    /**
     * Internal method to insert into a subtree.
     * x is the item to insert.
     * t is the node that roots the subtree.
     * Set the new root of the subtree.
     */
    void insert( Comparable &amp;&amp; x, BinaryNode * &amp; t )
    {
        if( t == nullptr )
            t = new BinaryNode{ std::move( x ), nullptr, nullptr };
        else if( x &lt; t-&gt;element )
            insert( std::move( x ), t-&gt;left );
        else if( t-&gt;element &lt; x )
            insert( std::move( x ), t-&gt;right );
        else
            ;  // Duplicate; do nothing
    }

    /**
     * Internal method to remove from a subtree.
     * x is the item to remove.
     * t is the node that roots the subtree.
     * Set the new root of the subtree.
     */
    void remove( const Comparable &amp; x, BinaryNode * &amp; t )
    {
        if( t == nullptr )
            return;   // Item not found; do nothing
        if( x &lt; t-&gt;element )
            remove( x, t-&gt;left );
        else if( t-&gt;element &lt; x )
            remove( x, t-&gt;right );
        else if( t-&gt;left != nullptr &amp;&amp; t-&gt;right != nullptr ) // Two children
        {
            t-&gt;element = findMin( t-&gt;right )-&gt;element;
            remove( t-&gt;element, t-&gt;right );
        }
        else
        {
            BinaryNode *oldNode = t;
            t = ( t-&gt;left != nullptr ) ? t-&gt;left : t-&gt;right;
            delete oldNode;
        }
    }

    /**
     * Internal method to find the smallest item in a subtree t.
     * Return node containing the smallest item.
     */
    BinaryNode * findMin( BinaryNode *t ) const
    {
        if( t == nullptr )
            return nullptr;
        if( t-&gt;left == nullptr )
            return t;
        return findMin( t-&gt;left );
    }

    /**
     * Internal method to find the largest item in a subtree t.
     * Return node containing the largest item.
     */
    BinaryNode * findMax( BinaryNode *t ) const
    {
        if( t != nullptr )
            while( t-&gt;right != nullptr )
                t = t-&gt;right;
        return t;
    }


    /**
     * Internal method to test if an item is in a subtree.
     * x is item to search for.
     * t is the node that roots the subtree.
     */
    bool contains( const Comparable &amp; x, BinaryNode *t ) const
    {
        if( t == nullptr )
            return false;
        else if( x &lt; t-&gt;element )
            return contains( x, t-&gt;left );
        else if( t-&gt;element &lt; x )
            return contains( x, t-&gt;right );
        else
            return true;    // Match
    }
/****** NONRECURSIVE VERSION*************************
    bool contains( const Comparable &amp; x, BinaryNode *t ) const
    {
        while( t != nullptr )
            if( x &lt; t-&gt;element )
                t = t-&gt;left;
            else if( t-&gt;element &lt; x )
                t = t-&gt;right;
            else
                return true;    // Match

        return false;   // No match
    }
*****************************************************/

    /**
     * Internal method to make subtree empty.
     */
    void makeEmpty( BinaryNode * &amp; t )
    {
        if( t != nullptr )
        {
            makeEmpty( t-&gt;left );
            makeEmpty( t-&gt;right );
            delete t;
        }
        t = nullptr;
    }

    /**
     * Internal method to print a subtree rooted at t in sorted order.
     */
    void printTree( BinaryNode *t, ostream &amp; out ) const
    {
        if( t != nullptr )
        {
            printTree( t-&gt;left, out );
            out &lt;&lt; t-&gt;element &lt;&lt; endl;
            printTree( t-&gt;right, out );
        }
    }

    /**
     * Internal method to clone subtree.
     */
    BinaryNode * clone( BinaryNode *t ) const
    {
        if( t == nullptr )
            return nullptr;
        else
            return new BinaryNode{ t-&gt;element, clone( t-&gt;left ), clone( t-&gt;right ) };
    }

  public:
    /**
     * Serialize the tree to a sorted vector.
     */
    std::vector&lt;Comparable&gt; serialize() const;

    /**
     * Deserialize from a sorted vector to build a balanced tree.
     */
    void deserialize(const std::vector&lt;Comparable&gt; &amp;vec);

    /**
     * Remove all elements in the range [L, R] and return the count.
     */
    int removeRange(const Comparable&amp; L, const Comparable&amp; R);
};
#endif
</code></pre>

C++<pre><code>#ifndef AVL_TREE_H
#define AVL_TREE_H

class UnderflowException { };
class IllegalArgumentException { };
class ArrayIndexOutOfBoundsException { };
class IteratorOutOfBoundsException { };
class IteratorMismatchException { };
class IteratorUninitializedException { };

#include &lt;algorithm&gt;
#include &lt;iostream&gt; 
using namespace std;

// AvlTree class
//
// CONSTRUCTION: zero parameter
//
// ******************PUBLIC OPERATIONS*********************
// void insert( x )       --&gt; Insert x
// void remove( x )       --&gt; Remove x (unimplemented)
// bool contains( x )     --&gt; Return true if x is present
// Comparable findMin( )  --&gt; Return smallest item
// Comparable findMax( )  --&gt; Return largest item
// boolean isEmpty( )     --&gt; Return true if empty; else false
// void makeEmpty( )      --&gt; Remove all items
// void printTree( )      --&gt; Print tree in sorted order
// ******************ERRORS********************************
// Throws UnderflowException as warranted

template &lt;typename Comparable&gt;
class AvlTree
{
  public:
    AvlTree( ) : root{ nullptr }
      { }

    AvlTree( const AvlTree &amp; rhs ) : root{ nullptr }
    {
        root = clone( rhs.root );
    }

    AvlTree( AvlTree &amp;&amp; rhs ) : root{ rhs.root }
    {
        rhs.root = nullptr;
    }

    ~AvlTree( )
    {
        makeEmpty( );
    }

    /**
     * Deep copy.
     */
    AvlTree &amp; operator=( const AvlTree &amp; rhs )
    {
        AvlTree copy = rhs;
        std::swap( *this, copy );
        return *this;
    }

    /**
     * Move.
     */
    AvlTree &amp; operator=( AvlTree &amp;&amp; rhs )
    {
        std::swap( root, rhs.root );

        return *this;
    }

    /**
     * Find the smallest item in the tree.
     * Throw UnderflowException if empty.
     */
    const Comparable &amp; findMin( ) const
    {
        if( isEmpty( ) )
            throw UnderflowException{ };
        return findMin( root )-&gt;element;
    }

    /**
     * Find the largest item in the tree.
     * Throw UnderflowException if empty.
     */
    const Comparable &amp; findMax( ) const
    {
        if( isEmpty( ) )
            throw UnderflowException{ };
        return findMax( root )-&gt;element;
    }

    /**
     * Returns true if x is found in the tree.
     */
    bool contains( const Comparable &amp; x ) const
    {
        return contains( x, root );
    }

    /**
     * Test if the tree is logically empty.
     * Return true if empty, false otherwise.
     */
    bool isEmpty( ) const
    {
        return root == nullptr;
    }

    /**
     * Print the tree contents in sorted order.
     */
    void printTree( ) const
    {
        if( isEmpty( ) )
            cout &lt;&lt; "Empty tree" &lt;&lt; endl;
        else
            printTree( root );
    }

    /**
     * Make the tree logically empty.
     */
    void makeEmpty( )
    {
        makeEmpty( root );
    }

    /**
     * Insert x into the tree; duplicates are ignored.
     */
    void insert( const Comparable &amp; x )
    {
        insert( x, root );
    }

    /**
     * Insert x into the tree; duplicates are ignored.
     */
    void insert( Comparable &amp;&amp; x )
    {
        insert( std::move( x ), root );
    }

    /**
     * Remove x from the tree. Nothing is done if x is not found.
     */
    void remove( const Comparable &amp; x )
    {
        remove( x, root );
    }

  private:
    struct AvlNode
    {
        Comparable element;
        AvlNode   *left;
        AvlNode   *right;
        int       height;

        AvlNode( const Comparable &amp; ele, AvlNode *lt, AvlNode *rt, int h = 0 )
          : element{ ele }, left{ lt }, right{ rt }, height{ h } { }

        AvlNode( Comparable &amp;&amp; ele, AvlNode *lt, AvlNode *rt, int h = 0 )
          : element{ std::move( ele ) }, left{ lt }, right{ rt }, height{ h } { }
    };

    AvlNode *root;


    /**
     * Internal method to insert into a subtree.
     * x is the item to insert.
     * t is the node that roots the subtree.
     * Set the new root of the subtree.
     */
    void insert( const Comparable &amp; x, AvlNode * &amp; t )
    {
        if( t == nullptr )
            t = new AvlNode{ x, nullptr, nullptr };
        else if( x &lt; t-&gt;element )
            insert( x, t-&gt;left );
        else if( t-&gt;element &lt; x )
            insert( x, t-&gt;right );

        balance( t );
    }

    /**
     * Internal method to insert into a subtree.
     * x is the item to insert.
     * t is the node that roots the subtree.
     * Set the new root of the subtree.
     */
    void insert( Comparable &amp;&amp; x, AvlNode * &amp; t )
    {
        if( t == nullptr )
            t = new AvlNode{ std::move( x ), nullptr, nullptr };
        else if( x &lt; t-&gt;element )
            insert( std::move( x ), t-&gt;left );
        else if( t-&gt;element &lt; x )
            insert( std::move( x ), t-&gt;right );

        balance( t );
    }

    /**
     * Internal method to remove from a subtree.
     * x is the item to remove.
     * t is the node that roots the subtree.
     * Set the new root of the subtree.
     */
    void remove( const Comparable &amp; x, AvlNode * &amp; t )
    {
        if( t == nullptr )
            return;   // Item not found; do nothing

        if( x &lt; t-&gt;element )
            remove( x, t-&gt;left );
        else if( t-&gt;element &lt; x )
            remove( x, t-&gt;right );
        else if( t-&gt;left != nullptr &amp;&amp; t-&gt;right != nullptr ) // Two children
        {
            t-&gt;element = findMin( t-&gt;right )-&gt;element;
            remove( t-&gt;element, t-&gt;right );
        }
        else
        {
            AvlNode *oldNode = t;
            t = ( t-&gt;left != nullptr ) ? t-&gt;left : t-&gt;right;
            delete oldNode;
        }

        balance( t );
    }

    static const int ALLOWED_IMBALANCE = 1;

    // Assume t is balanced or within one of being balanced
    void balance( AvlNode * &amp; t )
    {
        if( t == nullptr )
            return;

        if( height( t-&gt;left ) - height( t-&gt;right ) &gt; ALLOWED_IMBALANCE )
            if( height( t-&gt;left-&gt;left ) &gt;= height( t-&gt;left-&gt;right ) )
                rotateWithLeftChild( t );
            else
                doubleWithLeftChild( t );
        else
        if( height( t-&gt;right ) - height( t-&gt;left ) &gt; ALLOWED_IMBALANCE )
            if( height( t-&gt;right-&gt;right ) &gt;= height( t-&gt;right-&gt;left ) )
                rotateWithRightChild( t );
            else
                doubleWithRightChild( t );

        t-&gt;height = max( height( t-&gt;left ), height( t-&gt;right ) ) + 1;
    }

    /**
     * Internal method to find the smallest item in a subtree t.
     * Return node containing the smallest item.
     */
    AvlNode * findMin( AvlNode *t ) const
    {
        if( t == nullptr )
            return nullptr;
        if( t-&gt;left == nullptr )
            return t;
        return findMin( t-&gt;left );
    }

    /**
     * Internal method to find the largest item in a subtree t.
     * Return node containing the largest item.
     */
    AvlNode * findMax( AvlNode *t ) const
    {
        if( t != nullptr )
            while( t-&gt;right != nullptr )
                t = t-&gt;right;
        return t;
    }


    /**
     * Internal method to test if an item is in a subtree.
     * x is item to search for.
     * t is the node that roots the tree.
     */
    bool contains( const Comparable &amp; x, AvlNode *t ) const
    {
        if( t == nullptr )
            return false;
        else if( x &lt; t-&gt;element )
            return contains( x, t-&gt;left );
        else if( t-&gt;element &lt; x )
            return contains( x, t-&gt;right );
        else
            return true;    // Match
    }
/****** NONRECURSIVE VERSION*************************
    bool contains( const Comparable &amp; x, AvlNode *t ) const
    {
        while( t != nullptr )
            if( x &lt; t-&gt;element )
                t = t-&gt;left;
            else if( t-&gt;element &lt; x )
                t = t-&gt;right;
            else
                return true;    // Match

        return false;   // No match
    }
*****************************************************/

    /**
     * Internal method to make subtree empty.
     */
    void makeEmpty( AvlNode * &amp; t )
    {
        if( t != nullptr )
        {
            makeEmpty( t-&gt;left );
            makeEmpty( t-&gt;right );
            delete t;
        }
        t = nullptr;
    }

    /**
     * Internal method to print a subtree rooted at t in sorted order.
     */
    void printTree( AvlNode *t ) const
    {
        if( t != nullptr )
        {
            printTree( t-&gt;left );
            cout &lt;&lt; t-&gt;element &lt;&lt; endl;
            printTree( t-&gt;right );
        }
    }

    /**
     * Internal method to clone subtree.
     */
    AvlNode * clone( AvlNode *t ) const
    {
        if( t == nullptr )
            return nullptr;
        else
            return new AvlNode{ t-&gt;element, clone( t-&gt;left ), clone( t-&gt;right ), t-&gt;height };
    }
        // Avl manipulations
    /**
     * Return the height of node t or -1 if nullptr.
     */
    int height( AvlNode *t ) const
    {
        return t == nullptr ? -1 : t-&gt;height;
    }

    int max( int lhs, int rhs ) const
    {
        return lhs &gt; rhs ? lhs : rhs;
    }

    /**
     * Rotate binary tree node with left child.
     * For AVL trees, this is a single rotation for case 1.
     * Update heights, then set new root.
     */
    void rotateWithLeftChild( AvlNode * &amp; k2 )
    {
        AvlNode *k1 = k2-&gt;left;
        k2-&gt;left = k1-&gt;right;
        k1-&gt;right = k2;
        k2-&gt;height = max( height( k2-&gt;left ), height( k2-&gt;right ) ) + 1;
        k1-&gt;height = max( height( k1-&gt;left ), k2-&gt;height ) + 1;
        k2 = k1;
    }

    /**
     * Rotate binary tree node with right child.
     * For AVL trees, this is a single rotation for case 4.
     * Update heights, then set new root.
     */
    void rotateWithRightChild( AvlNode * &amp; k1 )
    {
        AvlNode *k2 = k1-&gt;right;
        k1-&gt;right = k2-&gt;left;
        k2-&gt;left = k1;
        k1-&gt;height = max( height( k1-&gt;left ), height( k1-&gt;right ) ) + 1;
        k2-&gt;height = max( height( k2-&gt;right ), k1-&gt;height ) + 1;
        k1 = k2;
    }

    /**
     * Double rotate binary tree node: first left child.
     * with its right child; then node k3 with new left child.
     * For AVL trees, this is a double rotation for case 2.
     * Update heights, then set new root.
     */
    void doubleWithLeftChild( AvlNode * &amp; k3 )
    {
        rotateWithRightChild( k3-&gt;left );
        rotateWithLeftChild( k3 );
    }

    /**
     * Double rotate binary tree node: first right child.
     * with its left child; then node k1 with new right child.
     * For AVL trees, this is a double rotation for case 3.
     * Update heights, then set new root.
     */
    void doubleWithRightChild( AvlNode * &amp; k1 )
    {
        rotateWithLeftChild( k1-&gt;right );
        rotateWithRightChild( k1 );
    }
};

#endif
