using namespace std;

void makeHeap(vector&lt;int&gt; &amp;a) {
    int n = a.size();
    for (int i = n / 2 - 1; i &gt;= 0; --i) {
        int node = i;
        while (1) {
            int left = 2 * node + 1;
            if (left &gt;= n) break;
            int right = left + 1;
            int target = left;
            if (right &lt; n &amp;&amp; a[right] &gt; a[left]) {
                target = right;
            }
            if (a[node] &gt;= a[target]) break; // 堆性质已满足
            swap(a[node], a[target]); // 下沉操作
            node = target;
        }
    }
}
