C++<pre><code>#include &lt;vector&gt;
using namespace std;

template &lt;typename Comparable&gt;
void Qs(vector&lt;Comparable&gt; &amp;a, int l, int r) {
    if (r - l &lt;= 1e2) {
        for (int i = l + 1; i &lt;= r; i++) {
            Comparable key = a[i];
            int j = i - 1;
            while (j &gt;= l &amp;&amp; a[j] &gt; key) {
                a[j + 1] = a[j];
                j--;
            }
            a[j + 1] = key;
        }
        return;
    }
    if (l &gt;= r) return;
    Comparable pivot = a[(l + r) / 2];
    int i = l, j = r;
    while (i &lt;= j) {
        while (a[i] &lt; pivot) i++;
        while (a[j] &gt; pivot) j--;
        if (i &lt;= j) {
            swap(a[i], a[j]);
            i++;
            j--;
        }
    }
    if (l &lt; j) Qs(a, l, j);
    if (i &lt; r) Qs(a, i, r);
}

template &lt;typename Comparable&gt;
void quick_sort(vector&lt;Comparable&gt; &amp;a) {
    int len = a.size();
    Qs(a, 0, len - 1);
}
